import React from 'react';
import RecatDOM from 'react-dom';
import Card from './Card';
import Button from './Button';
import classes from './ErrorModal.module.css';

const BackDrop=props=>{
  return  <div className={classes.backdrop} onClick={props.onConfirm} />
}

const Modal=props=>{
return(
  <Card className={classes.modal}>
  <header className={classes.header}>
    <h2>{props.title}</h2>
  </header>
  <div className={classes.content}>
    <p>{props.message}</p>
  </div>
  <footer className={classes.actions}>
    <Button onClick={props.onConfirm}>Okay</Button>
  </footer>
</Card>
)
}

const ErrorModal = (props) => {
  return (
    <React.Fragment>
    {RecatDOM.createPortal(<BackDrop onConfirm={props.onConfirm}/>,document.getElementById('backdrop-root'))}
     {RecatDOM.createPortal(<Modal title={props.title} message={props.message} onConfirm={props.onConfirm}/>,document.getElementById('overlay-root'))};
    </React.Fragment>
  );
};

export default ErrorModal;
